mapper.select:
SELECT m_id,s_id,m_name,m_gender,m_boss_name,m_boss_phone,m_is_cooperation,m_del,m_updatetime,m_createtime FROM base_vegetables_purchase_source_merchant

    and m_id = #{m_id}
    and s_id = #{s_id}
    and m_name CONCAT('%',#{m_name},'%')
    and m_gender = #{m_gender}
    and m_boss_name CONCAT('%',#{m_boss_name},'%')
    and m_boss_phone CONCAT('%',#{m_boss_phone},'%')
    and m_is_cooperation = #{m_is_cooperation}
    and m_del = #{m_del}
    and m_updatetime BETWEEN #{m_updatetime} AND #{m_updatetime}
    and m_createtime BETWEEN #{m_createtime} AND #{m_createtime}

mapper.update:
UPDATE base_vegetables_purchase_source_merchant
m_name = #{m_name}, s_id = #{s_id}, m_gender = #{m_gender}, m_boss_name = #{m_boss_name}, m_boss_phone = #{m_boss_phone}, m_is_cooperation = #{m_is_cooperation}, m_del = #{m_del}, s_updatetime = now()
WHEN m_id = #{m_id}
