mapper.select:
SELECT temp2.*,bvc.c_name FROM (
SELECT temp1.*,bvps.s_name FROM (
SELECT temp.*,bvv.v_name,bvv.c_id FROM (
select q_id,v_id,s_id,DATE_FORMAT(q_investigation_date,'%Y-%m-%d %H:%i:%S') q_investigation_date,q_investigation_price,q_price_increase,
CASE q_del
WHEN '00' THEN '可用'
WHEN '01' THEN '已删除'
END q_del_name,q_del,
DATE_FORMAT(q_updatetime,'%Y-%m-%d %H:%i:%S') q_updatetime,DATE_FORMAT(q_createtime,'%Y-%m-%d %H:%i:%S') q_createtime from base_vegetables_quotation

    and q_id = #{q_id}
    and v_id = #{v_id}
    and s_id = #{s_id}
    and q_investigation_date  BETWEEN #{q_start_investigation_date} and #{q_end_investigation_date}
    and q_investigation_price BETWEEN #{q_start_investigation_price} AND #{q_end_investigation_price}
    and q_investigation_price >= #{q_start_investigation_price}
    and q_investigation_price >= #{q_end_investigation_price}
    and q_price_increase BETWEEN #{q_start_price_increase} AND #{q_end_price_increase}
    and q_price_increase >= #{q_start_price_increase}
    and q_price_increase >= #{q_end_price_increase}
    and q_del = #{q_del}
    and q_updatetime BETWEEN #{q_start_updatetime} and #{q_end_updatetime}
    and q_updatetime >= #{q_start_updatetime}
    and q_updatetime >= #{q_end_updatetime}
    and q_createtime BETWEEN #{q_start_createtime} and #{q_end_createtime}

) temp
INNER JOIN base_vegetables_varieties bvv on temp.v_id = bvv.v_id ) temp1
INNER JOIN base_vegetables_purchase_source bvps on bvps.s_id = temp1.s_id ) temp2
INNER JOIN base_vegetables_category bvc on bvc.c_id = temp2.c_id

mapper.update:
UPDATE base_vegetables_quotation
v_id = #{edit_v_id}, s_id = #{edit_s_id}, q_investigation_date = #{edit_q_investigation_date}, q_investigation_price = #{edit_q_investigation_price}, q_price_increase = #{edit_q_price_increase}, q_del = #{edit_q_del}, q_updatetime = now()
WHERE q_id = #{q_id}
