mapper.select:
SELECT * FROM (
SELECT temp.*,bvpsm.m_name,
CASE bvpsm.m_gender
WHEN 0 THEN '男'
WHEN 1 THEN '女' END m_gender,bvpsm.m_boss_name,bvpsm.m_boss_phone FROM(
SELECT vvvc.*,bvps.s_name FROM (
SELECT bvv.*,bvc.c_name FROM (
select v_id,c_id,s_id,v_name,v_price,CONCAT(v_mature_season,'月') v_mature_season,CONCAT(v_mature_end_season,'月') v_mature_end_season,
CASE v_is_back_season
WHEN '00' THEN '否'
WHEN '01' THEN '是'
END v_is_back_season_name,v_is_back_season,
CASE v_del
WHEN '00' THEN '可用'
WHEN '01' THEN '已删除'
END v_del_name,v_del,
DATE_FORMAT(v_updatetime,'%Y-%m-%d %H:%i:%S') v_updatetime,DATE_FORMAT(v_createtime,'%Y-%m-%d %H:%i:%S') v_createtime from base_vegetables_varieties

    and c_id = #{c_id}
    and s_id = #{s_id}
    and v_name LIKE CONCAT('%',#{v_name},'%')
    and v_price BETWEEN #{v_start_price} AND #{v_end_price}
    and v_price >= #{v_start_price}
    and v_price >= #{v_end_price}
    and v_mature_season = #{v_mature_season}
    and v_mature_end_season = #{v_mature_end_season}
    and v_is_back_season = #{v_is_back_season}
    and v_del = #{v_del}
    and v_updatetime BETWEEN #{v_start_updatetime} and #{v_end_updatetime}
    and v_updatetime >= #{v_start_updatetime}
    and v_updatetime >= #{v_end_updatetime}
    and v_createtime BETWEEN #{v_start_createtime} and #{v_end_createtime}

) bvv INNER JOIN base_vegetables_category bvc on bvv.c_id = bvc.c_id ) vvvc
INNER JOIN base_vegetables_purchase_source bvps on vvvc.s_id = bvps.s_id ) temp
LEFT JOIN base_vegetables_purchase_source_merchant bvpsm on temp.s_id = bvpsm.s_id
WHERE bvpsm.m_del = '00') ttable

    and m_name like CONCAT('%',#{m_name},'%') 
    and m_boss_name like CONCAT('%',#{m_boss_name},'%') 
    and m_boss_phone like CONCAT('%',#{m_boss_phone},'%')

mapper.update:
UPDATE base_vegetables_varieties
c_id = #{edit_c_id}, s_id = #{edit_s_id}, v_name = #{edit_v_name}, v_price = #{edit_v_price}, v_mature_season = #{edit_v_mature_season}, v_mature_end_season = #{edit_v_mature_end_season}, v_is_back_season = #{edit_v_is_back_season}, v_del = #{edit_v_del}, v_updatetime = now()
WHEN v_id = #{v_id}
